import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      clicked : 0,
    }
    this.onDivClicked = this.onDivClicked.bind(this);
  }
  onDivClicked(){
    this.setState( {clicked: this.state.clicked + 1 } );
  }
  render (){
    return(
      <div className="App">
        <h1 onClick={this.onDivClicked}> This Header is Clicked {this.clicked} times. </h1>
    </div>
    );
  }
}

export default App;
